import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("login").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Data)
def token = holder.accessToken
//Setting a varible in project level with the value
context.testCase.TestSuite.project.setPropertyValue("token","bearer "+token)

import groovy.json.JsonSlurper
def Data=context.testCase.getTestStepByName("Get All users").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Data)
def num=holder.count.toInteger()
for(def i=0;i<num;i++)
{
	if(holder.users[i].email=="jhk@gmail.com")
	{
		def userid=holder.users[i]._id
		def converted_userid=userid.toString()
		context.testCase.testSuite.project.setPropertyValue("userID",converted_userid)

	}
}

import groovy.json.JsonSlurper
def example = context.testCase.getTestStepByName("Get All Products").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(example)
def value= holder.count.toInteger()
log.info value
for(def i=0;i<value;i++)
{
	if(holder.products[i].name == "cooldrinks")
	{
		def  pid  = holder.products[i]._id
		log.info pid
		def	cpid = pid.toString()
		//context.testCase.testSuite.setPropertyValue("Products_Id", cpid)
		context.testCase.testSuite.project.setPropertyValue("Products_id", cpid)
	}
}

import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("Get all orders").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Data)
def t_num = holder.count.toInteger()
log.info t_num
for(def i=0;i<t_num;i++)
{
	if(holder.orders[i].product!=null)
	{
		if(holder.orders[i].product.name == "kitchen items")
		{
		def oid = holder.orders[i]._id;
		def orderid = oid.toString()
		context.testCase.testSuite.project.setPropertyValue("O_id", orderid)	
		}
	}
}

